targets.expr:
sum by (hostname) (
  rate(nginx_request_sec_duration_sum{
    env=~"prod",
    hostname=~"$hostname",
    ic_subnet_id=~"$ic_subnet",
}[$period]))
/
sum by (hostname) (
  rate(nginx_request_sec_duration_count{
    env=~"prod",
    hostname=~"$hostname",
    ic_subnet_id=~"$ic_subnet",
}[$period]))

sum by (status) (
  rate(nginx_request_total{
    env=~"prod",
    hostname=~"$hostname",
    ic_subnet_id=~"$ic_subnet",
  }[$period])
)

sum by(api_type) (
  quantile by(api_type, ic_subnet) (
    0.5,
    sum without(status) (
      rate(sandboxed_execution_executed_message_slices_total{job="replica",ic="$ic",ic_subnet=~"$ic_subnet",instance=~"$instance",api_type!="non replicated query"}[$period])
    )
  )
)

label_replace(
  quantile by (status, ic_subnet) (
    0.5,
    sum without(api_type) (
      rate(sandboxed_execution_executed_message_slices_total{job="replica",ic="$ic",ic_subnet=~"$ic_subnet",instance=~"$instance",api_type!="non replicated query",status!~"Success|NoResponse|Paused"}[$period])
    )
  ),
  "ic_subnet", "$1", "ic_subnet", "([a-z0-9]+)-.*"
)

label_replace(
  quantile by (status, ic_subnet) (
    0.5,
    sum without(api_type) (
      rate(sandboxed_execution_executed_message_slices_total{job="replica",ic="$ic",ic_subnet=~"$ic_subnet",instance=~"$instance",api_type="non replicated query",status!~"Success|NoResponse"}[$period])
    )
  ),
  "ic_subnet", "$1", "ic_subnet", "([a-z0-9]+)-.*"
)

sum by(status, type) (
  quantile by(ic, status, ic_subnet, type) (
    0.5,
    rate(mr_inducted_xnet_message_count{ic="$ic",ic_subnet=~"$ic_subnet",instance=~"$instance"}[$period])
  )
)

sum by(status, type) (
  quantile by(ic, status, ic_subnet) (
    0.5,
    rate(mr_inducted_ingress_message_count{ic="$ic",ic_subnet=~"$ic_subnet",instance=~"\\[?$dc:.*",instance=~"$instance"}[$period])
  )
)
